import React, { useState, useEffect } from 'react';
import { Box, Paper, Typography, Grid, Card, CardContent, Chip } from '@mui/material';
import axios from 'axios';

const API_BASE_URL = process.env.REACT_APP_API_BASE_URL || 'http://localhost:8000';

function SystemStatus() {
  const [status, setStatus] = useState({
    orchestrator: 'unknown',
    memory: 'unknown',
    subagent: 'unknown',
    mcp: 'unknown',
    minio: 'unknown',
    ollama: 'unknown'
  });

  const [systemInfo, setSystemInfo] = useState({});

  useEffect(() => {
    const checkStatus = async () => {
      const services = [
        { name: 'orchestrator', url: `${API_BASE_URL}/health` },
        { name: 'memory', url: `http://localhost:8002/health` },
        { name: 'subagent', url: `http://localhost:8003/health` },
        { name: 'mcp', url: `http://localhost:8080/health` },
        { name: 'minio', url: `http://localhost:9000/minio/health/live` }
      ];

      const newStatus = { ...status };

      for (const service of services) {
        try {
          const response = await axios.get(service.url, { timeout: 5000 });
          newStatus[service.name] = response.status === 200 ? 'healthy' : 'unhealthy';
        } catch (error) {
          newStatus[service.name] = 'unhealthy';
        }
      }

      // Check Ollama
      try {
        const response = await axios.get('http://localhost:11434/api/tags', { timeout: 5000 });
        newStatus.ollama = response.status === 200 ? 'healthy' : 'unhealthy';
      } catch (error) {
        newStatus.ollama = 'unhealthy';
      }

      setStatus(newStatus);
    };

    checkStatus();
    const interval = setInterval(checkStatus, 30000); // Check every 30 seconds

    return () => clearInterval(interval);
  }, []);

  const getStatusColor = (status) => {
    switch (status) {
      case 'healthy': return 'success';
      case 'unhealthy': return 'error';
      default: return 'warning';
    }
  };

  const services = [
    { name: 'Orchestrator', key: 'orchestrator', port: 8000 },
    { name: 'Memory Service', key: 'memory', port: 8002 },
    { name: 'Subagent Manager', key: 'subagent', port: 8003 },
    { name: 'MCP Gateway', key: 'mcp', port: 8080 },
    { name: 'MinIO', key: 'minio', port: 9000 },
    { name: 'Ollama', key: 'ollama', port: 11434 }
  ];

  return (
    <Paper sx={{ p: 3 }}>
      <Typography variant="h6" gutterBottom>
        System Status
      </Typography>

      <Grid container spacing={2}>
        {services.map((service) => (
          <Grid item xs={12} sm={6} md={4} key={service.key}>
            <Card>
              <CardContent>
                <Typography variant="h6" component="div">
                  {service.name}
                </Typography>
                <Typography color="text.secondary" gutterBottom>
                  Port: {service.port}
                </Typography>
                <Chip
                  label={status[service.key]}
                  color={getStatusColor(status[service.key])}
                  size="small"
                />
              </CardContent>
            </Card>
          </Grid>
        ))}
      </Grid>

      <Box sx={{ mt: 3 }}>
        <Typography variant="h6" gutterBottom>
          System Information
        </Typography>
        <Typography variant="body2" color="text.secondary">
          Last updated: {new Date().toLocaleString()}
        </Typography>
        <Typography variant="body2" color="text.secondary">
          API Base URL: {API_BASE_URL}
        </Typography>
      </Box>
    </Paper>
  );
}

export default SystemStatus;
